import React, { useState, useEffect } from "react";
import PostCard from '../PostCard/PostCard';
import './PostBoard.css';

export default function PostBoard(props) {
    const categoryBoard = props.category;
    const [posts, setPosts] = useState([]);

    useEffect(() => {
        fetch("http://localhost:9991/")
            .then(res => res.json())
            .then((result) => {
                setPosts(result);
            })
    }, [])


    return (
        <div className="categoryContainer" bg="light">
            <h1>{props.category}<span class="material-symbols-outlined">Edit</span></h1>
            {posts.map((post) => (
                <PostCard>
                    title:{post.title}
                    category:{post.category}
                    message:{post.message}
                </PostCard>
            ))}
        </div>
    )
}
