import React, { useState, useEffect } from "react";
import PostCard from '../PostCard/PostCard';
import './PostBoard.css';

export default function PostBoard(props) {
    const categoryBoard = props.category;
    const [posts, setPosts] = useState([]);

    if (categoryBoard === "Question") {
        return (
            <div className="categoryContainer" bg="light">
                <h1>{props.category}<span class="material-symbols-outlined">question_mark</span></h1>
                <PostCard
                    title="chainsaw"
                    category="anime"
                    message="ta wapo"
                    image="uwu"
                />
            </div>
        );
    }
    else if (categoryBoard === "Suggestion") {
        return (
            <div className="categoryContainer" bg="light"><h1>{props.category}<span class="material-symbols-outlined">speaker</span></h1>
                <PostCard
                    title="chainsaw"
                    category="anime"
                    message="ta wapo"
                    image="uwu"
                />
            </div>
        );
    }
    else {
        return (
            <div className="categoryContainer" bg="light"><h1>{props.category}<span class="material-symbols-outlined">edit</span></h1>
                <PostCard
                    title="chainsaw"
                    category="anime"
                    message="ta wapo"
                    image="uwu"
                />
            </div>
        );
    }
}
