import React from "react";
import './PostCard.css';

export default function PostCard(props) {

    if (props.category === "Question") {
        return (
            <div className="cardContainer">
                <h1>{props.title}</h1>
                <h3>{props.category}<span class="material-symbols-outlined">
                    question_mark
                </span></h3>
                <p>{props.message}</p>
            </div>
        );
    }

    else if (props.category === "Suggestion") {
        return (
            <div className="cardContainer">
                <h1>{props.title}</h1>
                <h3>{props.category}<span class="material-symbols-outlined">
                    edit
                </span></h3>
                <p>{props.message}</p>
            </div>
        );
    }

    else if (props.category === "Clarification") {
        return (
            <div className="cardContainer">
                <h1>{props.title}</h1>
                <h3>{props.category}<span class="material-symbols-outlined">
                    speaker
                </span></h3>
                <p>{props.message}</p>
            </div>
        );
    }
    else {
        <h1>Hubo un error</h1>
    }


}
