import React from "react";
import PostCard from '../PostCard/PostCard';
import './PostBoard.css';

export default function PostBoard(props) {

    const categoryBoard = props.category;

    if (categoryBoard == "Question") {
        return (
            <div className="categoryContainer" bg="light">
                <h1>{props.category}</h1>
                <span class="material-symbols-outlined">
                    question_mark
                </span>
                <PostCard
                    title="chainsaw"
                    category="anime"
                    message="ta wapo"
                />
            </div>
        );
    }
    else if (categoryBoard == "Suggestion") {
        return (
            <div className="categoryContainer" bg="light">
                <h1>{props.category}</h1>
                <span class="material-symbols-outlined">
                    speaker
                </span>
                <PostCard
                    title="chainsaw"
                    category="anime"
                    message="ta wapo"
                />
            </div>
        );
    }
    else {
        return (
            <div className="categoryContainer" bg="light">
                <h1>{props.category}</h1>
                <span class="material-symbols-outlined">
                    edit
                </span>
                <PostCard
                    title="chainsaw"
                    category="anime"
                    message="ta wapo"
                />
            </div>
        );
    }


    return (
        <>
            <div className="categoryContainer" bg="light">
                <h1>{props.category}</h1>
                if (props.category == Question ){
                    <span class="material-symbols-outlined">
                        question_mark
                    </span>
                }
                else if (props.category == Suggestion){
                    <span class="material-symbols-outlined">
                        speaker
                    </span>
                }
                else {
                    <span class="material-symbols-outlined">
                        edit
                    </span>
                }
                <PostCard
                    title="chainsaw"
                    category="anime"
                    message="ta wapo"
                />
            </div>
        </>
    );
}
